mapper.select:
select * from file_record_table


        relative_location = #{relativePath}

    and deleted = 0

select * from file_record_table

    relative_location = #{relativePath}
    and file_name = #{filename}
    and deleted = 0

mapper.update:
update file_record_table
file_name = #{fileName}, relative_location = #{relativeLocation}, md5 = #{md5}, last_modified = #{lastModified}, `size` = #{size}
where id = #{id}

select.foreach:
select * from file_record_table

    relative_location = #{item.relativeLocation}
    and file_name = #{item.fileName}
    and deleted = 0
